    private void btnLanciaMonetaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLanciaMonetaActionPerformed
        // TODO add your handling code here:
        String g1 = "Giocatore 1", g2 = "Giocatore 2";
        Tris t = new Tris(g1, g2);

        int ris = t.moneta();

        if (ris == 0) {
            txtMoneta.setText(g1);
            txtTurno1.setText(g1);
        } else {
            txtMoneta.setText(g2);
            txtTurno1.setText(g2);
        }
        btnLanciaMoneta.setEnabled(false);
    }//GEN-LAST:event_btnLanciaMonetaActionPerformed

