    private void btn1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn1ActionPerformed
        // TODO add your handling code here:
        String g1 = "Giocatore 1", g2 = "Giocatore 2";
        Tris t = new Tris(g1, g2);

        int posX = 0, posY = 0;
        btn1.setEnabled(false);
        if (txtTurno1.getText().equals("Giocatore 1")) {
            if (t.mossaG1(posX, posY) == true) {
                txtTurno1.setText(g2);
                btn1.setText("X");
            }
        } else {
            if (t.mossaG2(posX, posY) == true) {
                txtTurno1.setText(g1);
                btn1.setText("O");
            }
        }
        if(t.controllo(t.getCampoDaGioco()) == true){
            if(txtTurno1.getText().equals("Giocatore 1")){
                txtVincitore1.setText(g2);
            }else{
                txtVincitore1.setText(g1);
            }
        }
    }//GEN-LAST:event_btn1ActionPerformed

